package Com;

public class Swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int a=10;
        int b=20;
        int i=0;
        System.out.println("Before Swap a is "+a+" and b is "+b);
        i=a;
        a=b;
        b=i;
        System.out.println("After Swap a is "+a+" and b is "+b);
	}

}
